import React from "react";
import { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const [note, setNote] = useState(
    {
      noteHeading: "",
      noteTexts: ""
    }
  );

  const [noteArray, setNoteArray] = useState([]);

  function addNote() {
    setNoteArray(lastValue => { return [...lastValue, note]; });
    //const newList = noteArray.concat({note});
    //setNoteArray(newList);
    setNote("");
  }

  function handleInput(event) {
    //const newValue = event.target.value;
    const {name,value}=event.target;
    //setNote(newValue);

    setNote(lastNoteValue=>{
      return{
        ...lastNoteValue,
        [name]:value
      }
    });
    //console.log(newValue);
    event.preventDefault;
  }

  function deleteItem(id) {
    setItems(lastItems => {
      return lastItems.filter((item, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <Header />
      <CreateArea
        add={addNote}
        input={handleInput}
        noteTitle={note.noteHeading}
        noteContent={note.noteTexts}
      />
        {noteArray.map((todo, index) =>
        (
          <Note
            key={index}
            id={index}
            title={todo.noteHeading}
            content={todo.noteTexts}
            delete={deleteItem}
          />
        ))}
      <Footer />
    </div>
  );
}

export default App;
