import { useState } from "react";
import React from "react";


function CreateArea(props) {

  return (
    <div>
      <form>
        <input onChange={props.input} name="title" placeholder="Title" />
        <textarea onChange={input} name="content" placeholder="Take a note..." rows="3" />
        <button onClick={props.add}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
