import React from "react";
import { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  var noteObj = {
    noteHeading: "",
    noteTexts: ""
  };

  const [note, setNote] = useState("");
  const [noteArray, setNoteArray] = useState([]);

  function addNote() {
    setNoteArray(lastValue => { return [...lastValue, note]; });
    setNote("");
    console.log(note);
  }

  function handleInput(event) {
    const newValue = event.target.value;
    setNote(newValue);
    //console.log(newValue);
  }

  function deleteItem(id) {
    setItems(lastItems => {
      return lastItems.filter((item, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <Header />
      <CreateArea
        add={addNote}
        input={handleInput}
        text={note}
      />

      {noteArray.map((todo, index) =>
      (
        <Note
          key={index}
          id={index}
          title={todo}
          content={todo}
          delete={deleteItem}
        />
      ))}
      <Footer />
    </div>
  );
}

export default App;
