import React from "react";
import { useState } from "react";


function CreateArea(props) {

  const [note, setNote] = useState(
    {
      noteHeading: "",
      noteTexts: ""
    }
  );

  function handleInput(event) {
    //const newValue = event.target.value;
    const {name,value}=event.target;
    //setNote(newValue);

    setNote(lastNoteValue=>{
      return{
        ...lastNoteValue,
        [name]:value
      }
    });
    //console.log(newValue);
    event.preventDefault();

  }

  function addNote(event) {
    setNoteArray(lastValue => { return [...lastValue, note]; });
    //const newList = noteArray.concat({note});
    //setNoteArray(newList);
    setNote("");
    event.preventDefault();
  }

  return (
    <div>
      <form>
        <input onChange={handleInput} name="title" placeholder="Title" value={note.noteTitle}/>
        <textarea onChange={handleInput} name="content" placeholder="Take a note..." value={note.noteContent}rows="3" />
        <button onClick={addNote}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
